import React, { useState, useEffect } from 'react';
import api from '../../utils/axiosConfig';

const AuthenticatedPreview = ({ certificate, userRole, onError }) => {
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // More robust file type detection
  const fileType = certificate.fileType?.toLowerCase() || '';
  const fileName = certificate.fileName?.toLowerCase() || '';
  const isPDF = fileType.includes('pdf') || fileName.endsWith('.pdf');
  const isImage = fileType.includes('image') || 
                  fileType.includes('jpeg') || 
                  fileType.includes('jpg') || 
                  fileType.includes('png') ||
                  fileName.endsWith('.jpg') ||
                  fileName.endsWith('.jpeg') ||
                  fileName.endsWith('.png');

  const getFileTypeIcon = (fileType) => {
    if (fileType?.includes('pdf')) return 'ðŸ“„';
    if (fileType?.includes('image')) return 'ðŸ–¼ï¸';
    return 'ðŸ“Ž';
  };

  useEffect(() => {
    const loadPreview = async () => {
      try {
        setLoading(true);
        setError(false);

        const token = localStorage.getItem('token');
        if (!token) {
          setError(true);
          setLoading(false);
          return;
        }

        const baseUrl = userRole === 'STUDENT' ? '/api/student' : '/api/staff';
        
        // Use axios with proper auth headers
        const response = await api.get(`${baseUrl}/certificates/${certificate.certificateId}/view`, {
          responseType: 'blob'
        });

        const blobUrl = URL.createObjectURL(response.data);
        setPreviewUrl(blobUrl);
      } catch (err) {
        console.error('Preview load error:', err);
        setError(true);
        onError?.(err);
      } finally {
        setLoading(false);
      }
    };

    if ((isImage || isPDF) && certificate.certificateId) {
      loadPreview();
    } else {
      setLoading(false);
    }

    // Cleanup function to revoke object URLs
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
      }
    };
  }, [certificate.certificateId, userRole, isImage, isPDF, onError]);

  if (loading) {
    return (
      <div className="preview-loading">
        <div className="loading-spinner-small"></div>
        <span>Loading preview...</span>
      </div>
    );
  }

  if (error || (!isImage && !isPDF)) {
    return (
      <div className="file-placeholder">
        <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
        <div className="file-info">
          <span className="file-name">{certificate.fileName}</span>
          <span className="file-type">{certificate.fileType}</span>
          {error && (
            <div>
              <span className="error-text">Preview failed to load</span>
              <button 
                onClick={() => {
                  const token = localStorage.getItem('token');
                  const baseUrl = userRole === 'STUDENT' ? '/api/student' : '/api/staff';
                  const url = `${baseUrl}/certificates/${certificate.certificateId}/view`;
                  window.open(`${url}?token=${token}`, '_blank');
                }}
                style={{
                  marginTop: '8px',
                  padding: '4px 8px',
                  fontSize: '12px',
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Open in New Tab
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  if (isImage && previewUrl) {
    return (
      <div className="image-preview-container">
        <img
          src={previewUrl}
          alt={certificate.certificateName}
          className="certificate-preview-image"
          onError={() => {
            console.error('Image failed to load:', previewUrl);
            setError(true);
            onError?.();
          }}
          onLoad={() => {
            console.log('Image loaded successfully:', certificate.fileName);
          }}
        />
      </div>
    );
  }

  if (isPDF && previewUrl) {
    return (
      <div className="pdf-preview-container">
        <iframe
          src={previewUrl}
          className="certificate-preview-pdf"
          title={certificate.certificateName}
          onError={() => {
            console.error('PDF failed to load:', previewUrl);
            setError(true);
            onError?.();
          }}
          onLoad={() => {
            console.log('PDF loaded successfully:', certificate.fileName);
          }}
        />
        <div className="pdf-overlay">
          <button 
            onClick={() => window.open(previewUrl, '_blank')}
            style={{
              padding: '8px 16px',
              background: 'rgba(0,0,0,0.8)',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            ðŸ“„ Open PDF
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="file-placeholder">
      <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
      <div className="file-info">
        <span className="file-name">{certificate.fileName}</span>
        <span className="file-type">No preview available</span>
      </div>
    </div>
  );
};

export default AuthenticatedPreview;
