import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import Header from './Layout/Header';
import Sidebar from './Layout/Sidebar';
import KPICard from './UI/KPICard';
import CertificateGrid from './UI/CertificateGrid';
import CertificateViewer from './CertificateViewer';
import axios from 'axios';

const StaffDashboard = () => {
  const { user } = useAuth();
  const [activeSection, setActiveSection] = useState('dashboard');
  const [certificates, setCertificates] = useState([]);
  const [analytics, setAnalytics] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedCertificate, setSelectedCertificate] = useState(null);
  const [remarks, setRemarks] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [viewingCertificate, setViewingCertificate] = useState(null);

  useEffect(() => {
    fetchCertificates();
  }, []);

  useEffect(() => {
    filterCertificates();
  }, [certificates, searchTerm, statusFilter]);

  const fetchCertificates = async () => {
    try {
      const response = await axios.get('/api/staff/certificates');
      setCertificates(response.data);
    } catch (error) {
      setError('Failed to fetch certificates');
    } finally {
      setLoading(false);
    }
  };

  const filterCertificates = () => {
    let filtered = certificates;

    if (searchTerm) {
      filtered = filtered.filter(cert => 
        cert.studentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        cert.certificateName.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (statusFilter !== 'ALL') {
      filtered = filtered.filter(cert => cert.status === statusFilter);
    }

    setFilteredCertificates(filtered);
  };

  const handleVerify = async (certificateId) => {
    try {
      await axios.put(`/api/staff/certificates/${certificateId}/verify`, {
        remarks: remarks
      });
      setMessage('Certificate verified successfully');
      setSelectedCertificate(null);
      setRemarks('');
      fetchCertificates();
    } catch (error) {
      setError('Verification failed');
    }
  };

  const handleReject = async (certificateId) => {
    try {
      await axios.put(`/api/staff/certificates/${certificateId}/reject`, {
        remarks: remarks
      });
      setMessage('Certificate rejected successfully');
      setSelectedCertificate(null);
      setRemarks('');
      fetchCertificates();
    } catch (error) {
      setError('Rejection failed');
    }
  };

  const openModal = (certificate) => {
    setSelectedCertificate(certificate);
    setRemarks(certificate.staffRemarks || '');
  };

  const closeModal = () => {
    setSelectedCertificate(null);
    setRemarks('');
  };

  const getStatusBadge = (status) => {
    const statusClass = `status-badge status-${status.toLowerCase()}`;
    return <span className={statusClass}>{status}</span>;
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Staff Dashboard</h1>
        <div>
          <span>Welcome, {user.fullName}</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </div>

      {message && <div className="success-message">{message}</div>}
      {error && <div className="error-message">{error}</div>}

      <div className="search-section">
        <h2>Search & Filter Certificates</h2>
        <div className="search-controls">
          <input
            type="text"
            placeholder="Search by student name or certificate name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <div className="filter-buttons">
            {['ALL', 'PENDING', 'VERIFIED', 'REJECTED'].map(status => (
              <button
                key={status}
                className={`filter-btn ${statusFilter === status ? 'active' : ''}`}
                onClick={() => setStatusFilter(status)}
              >
                {status}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div>
        <h2>Certificates ({filteredCertificates.length})</h2>
        <div className="certificates-grid">
          {filteredCertificates.map((cert) => (
            <div key={cert.certificateId} className="certificate-card">
              <h3>{cert.certificateName}</h3>
              <p><strong>Student:</strong> {cert.studentName}</p>
              <p><strong>Status:</strong> {getStatusBadge(cert.status)}</p>
              <p><strong>Uploaded:</strong> {new Date(cert.uploadDate).toLocaleDateString()}</p>
              {cert.staffRemarks && (
                <p><strong>Remarks:</strong> {cert.staffRemarks}</p>
              )}
              {cert.verifiedBy && (
                <p><strong>Verified by:</strong> {cert.verifiedBy}</p>
              )}
              <div style={{ marginTop: '1rem' }}>
                <button 
                  onClick={() => setViewingCertificate(cert)}
                  className="btn btn-view"
                  style={{ marginRight: '0.5rem' }}
                >
                  View
                </button>
                <button 
                  onClick={() => openModal(cert)}
                  className="btn btn-primary"
                >
                  Review
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {selectedCertificate && (
        <div className="modal">
          <div className="modal-content">
            <h3>Review Certificate: {selectedCertificate.certificateName}</h3>
            <p><strong>Student:</strong> {selectedCertificate.studentName}</p>
            <p><strong>Current Status:</strong> {getStatusBadge(selectedCertificate.status)}</p>
            
            <div className="form-group">
              <label>Remarks</label>
              <textarea
                value={remarks}
                onChange={(e) => setRemarks(e.target.value)}
                rows="4"
                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '5px' }}
                placeholder="Add your remarks here..."
              />
            </div>
            
            <div className="modal-actions">
              <button onClick={closeModal} className="btn">Cancel</button>
              <button 
                onClick={() => handleReject(selectedCertificate.certificateId)}
                className="btn btn-danger"
              >
                Reject
              </button>
              <button 
                onClick={() => handleVerify(selectedCertificate.certificateId)}
                className="btn btn-success"
              >
                Verify
              </button>
            </div>
          </div>
        </div>
      )}

      {viewingCertificate && (
        <CertificateViewer
          certificate={viewingCertificate}
          onClose={() => setViewingCertificate(null)}
          userRole="STAFF"
        />
      )}
    </div>
  );
};

export default StaffDashboard;
