import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';

const Sidebar = ({ activeSection, onSectionChange }) => {
  const { user } = useAuth();
  const [collapsed, setCollapsed] = useState(false);

  const studentMenuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { id: 'certificates', label: 'My Certificates', icon: 'üìú' },
    { id: 'upload', label: 'Upload Certificate', icon: 'üì§' },
    { id: 'settings', label: 'Settings', icon: '‚öôÔ∏è' }
  ];

  const staffMenuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { id: 'certificates', label: 'All Certificates', icon: 'üìú' },
    { id: 'verification', label: 'Verification Queue', icon: '‚úÖ' },
    { id: 'reports', label: 'Reports & Analytics', icon: 'üìà' },
    { id: 'settings', label: 'Settings', icon: '‚öôÔ∏è' }
  ];

  const menuItems = user?.role === 'STUDENT' ? studentMenuItems : staffMenuItems;

  return (
    <aside className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <button 
          className="collapse-btn"
          onClick={() => setCollapsed(!collapsed)}
        >
          {collapsed ? '‚Üí' : '‚Üê'}
        </button>
      </div>
      
      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <button
            key={item.id}
            className={`nav-item ${activeSection === item.id ? 'active' : ''}`}
            onClick={() => onSectionChange(item.id)}
          >
            <span className="nav-icon">{item.icon}</span>
            {!collapsed && <span className="nav-label">{item.label}</span>}
          </button>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
