import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AuthenticatedPreview = ({ certificate, userRole, onError }) => {
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  const isPDF = certificate.fileType?.includes('pdf');
  const isImage = certificate.fileType?.includes('image');

  const getFileTypeIcon = (fileType) => {
    if (fileType?.includes('pdf')) return 'ðŸ“„';
    if (fileType?.includes('image')) return 'ðŸ–¼ï¸';
    return 'ðŸ“Ž';
  };

  useEffect(() => {
    const loadPreview = async () => {
      try {
        setLoading(true);
        setError(false);

        const token = localStorage.getItem('token');
        if (!token) {
          setError(true);
          setLoading(false);
          return;
        }

        const baseUrl = userRole === 'STUDENT' ? '/api/student' : '/api/staff';
        
        // Try to load the preview with proper headers
        const response = await fetch(`${baseUrl}/certificates/${certificate.certificateId}/view`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': '*/*'
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        }

        const blob = await response.blob();
        const blobUrl = URL.createObjectURL(blob);
        setPreviewUrl(blobUrl);
      } catch (err) {
        console.error('Preview load error:', err);
        setError(true);
        onError?.(err);
      } finally {
        setLoading(false);
      }
    };

    if ((isImage || isPDF) && certificate.certificateId) {
      loadPreview();
    } else {
      setLoading(false);
    }

    // Cleanup function to revoke object URLs
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
      }
    };
  }, [certificate.certificateId, userRole, isImage, isPDF, onError]);

  if (loading) {
    return (
      <div className="preview-loading">
        <div className="loading-spinner-small"></div>
        <span>Loading preview...</span>
      </div>
    );
  }

  if (error || (!isImage && !isPDF)) {
    return (
      <div className="file-placeholder">
        <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
        <div className="file-info">
          <span className="file-name">{certificate.fileName}</span>
          <span className="file-type">{certificate.fileType}</span>
          {error && <span className="error-text">Preview failed to load</span>}
        </div>
      </div>
    );
  }

  if (isImage && previewUrl) {
    return (
      <img
        src={previewUrl}
        alt={certificate.certificateName}
        className="certificate-preview-image"
        onError={() => {
          setError(true);
          onError?.();
        }}
      />
    );
  }

  if (isPDF && previewUrl) {
    return (
      <iframe
        src={previewUrl}
        className="certificate-preview-pdf"
        title={certificate.certificateName}
        onError={() => {
          setError(true);
          onError?.();
        }}
      />
    );
  }

  return (
    <div className="file-placeholder">
      <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
      <div className="file-info">
        <span className="file-name">{certificate.fileName}</span>
        <span className="file-type">No preview available</span>
      </div>
    </div>
  );
};

export default AuthenticatedPreview;
