import React, { useState, useEffect } from 'react';
import api from '../../utils/axiosConfig';

const AuthenticatedPreview = ({ certificate, userRole, onError }) => {
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  // More robust file type detection
  const fileType = certificate.fileType?.toLowerCase() || '';
  const fileName = certificate.fileName?.toLowerCase() || '';
  const isPDF = fileType.includes('pdf') || fileName.endsWith('.pdf');
  const isImage = fileType.includes('image') || 
                  fileType.includes('jpeg') || 
                  fileType.includes('jpg') || 
                  fileType.includes('png') ||
                  fileName.endsWith('.jpg') ||
                  fileName.endsWith('.jpeg') ||
                  fileName.endsWith('.png');

  const getFileTypeIcon = (fileType) => {
    if (fileType?.includes('pdf')) return 'üìÑ';
    if (fileType?.includes('image')) return 'üñºÔ∏è';
    return 'üìé';
  };

  useEffect(() => {
    const loadPreview = async () => {
      try {
        setLoading(true);
        setError(false);

        const token = localStorage.getItem('token');
        if (!token) {
          console.warn('No token found for preview');
          setError(true);
          setLoading(false);
          return;
        }

        if (!certificate.certificateId) {
          console.warn('No certificate ID found');
          setError(true);
          setLoading(false);
          return;
        }

        const baseUrl = userRole === 'STUDENT' ? '/api/student' : '/api/staff';
        const url = `${baseUrl}/certificates/${certificate.certificateId}/view`;
        
        console.log('Loading preview from:', url);
        console.log('File type:', certificate.fileType, 'File name:', certificate.fileName);
        
        // Use axios with proper auth headers
        const response = await api.get(url, {
          responseType: 'blob'
        });

        if (!response.data || response.data.size === 0) {
          throw new Error('Empty response from server');
        }

        const blobUrl = URL.createObjectURL(response.data);
        console.log('Preview loaded successfully, blob URL created');
        setPreviewUrl(blobUrl);
      } catch (err) {
        console.error('Preview load error:', err);
        console.error('Error details:', {
          message: err.message,
          response: err.response?.status,
          certificateId: certificate.certificateId,
          fileType: certificate.fileType,
          fileName: certificate.fileName
        });
        setError(true);
        onError?.(err);
      } finally {
        setLoading(false);
      }
    };

    // Always try to load preview if we have a certificate ID
    // The file type check is just for display logic
    if (certificate.certificateId) {
      loadPreview();
    } else {
      console.warn('No certificate ID, skipping preview load');
      setLoading(false);
    }

    // Cleanup function to revoke object URLs
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
        setPreviewUrl(null);
      }
    };
  }, [certificate.certificateId, userRole, certificate.fileType, certificate.fileName]);

  if (loading) {
    return (
      <div className="preview-loading">
        <div className="loading-spinner-small"></div>
        <span>Loading preview...</span>
      </div>
    );
  }

  // Show placeholder if error or if we don't have a preview URL yet
  if (error || (!previewUrl && !loading)) {
    return (
      <div className="file-placeholder">
        <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
        <div className="file-info">
          <span className="file-name">{certificate.fileName || 'Unknown file'}</span>
          <span className="file-type">{certificate.fileType || 'Unknown type'}</span>
          {error && (
            <div style={{ marginTop: '8px' }}>
              <span className="error-text" style={{ display: 'block', marginBottom: '8px' }}>
                Preview failed to load
              </span>
              <button 
                onClick={() => {
                  const token = localStorage.getItem('token');
                  const baseUrl = userRole === 'STUDENT' ? '/api/student' : '/api/staff';
                  const url = `${baseUrl}/certificates/${certificate.certificateId}/view`;
                  // Create a link with auth header
                  const link = document.createElement('a');
                  link.href = url;
                  link.target = '_blank';
                  document.body.appendChild(link);
                  link.click();
                  document.body.removeChild(link);
                }}
                style={{
                  padding: '6px 12px',
                  fontSize: '12px',
                  background: '#3b82f6',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                üîç Open in New Tab
              </button>
            </div>
          )}
        </div>
      </div>
    );
  }

  // Show image preview
  if (isImage && previewUrl) {
    return (
      <div className="image-preview-container">
        <img
          src={previewUrl}
          alt={certificate.certificateName || certificate.fileName}
          className="certificate-preview-image"
          style={{ 
            width: '100%', 
            height: '100%', 
            objectFit: 'contain',
            display: 'block'
          }}
          onError={(e) => {
            console.error('Image failed to load:', previewUrl, e);
            setError(true);
            onError?.();
          }}
          onLoad={() => {
            console.log('Image loaded successfully:', certificate.fileName);
          }}
        />
      </div>
    );
  }

  // Show PDF preview
  if (isPDF && previewUrl) {
    return (
      <div className="pdf-preview-container" style={{ position: 'relative', width: '100%', height: '100%' }}>
        <iframe
          src={previewUrl}
          className="certificate-preview-pdf"
          title={certificate.certificateName || certificate.fileName}
          style={{
            width: '100%',
            height: '100%',
            border: 'none',
            display: 'block'
          }}
          onError={() => {
            console.error('PDF iframe failed to load:', previewUrl);
            setError(true);
            onError?.();
          }}
          onLoad={() => {
            console.log('PDF iframe loaded successfully:', certificate.fileName);
          }}
        />
        <div className="pdf-overlay" style={{
          position: 'absolute',
          top: '10px',
          right: '10px',
          opacity: 0,
          transition: 'opacity 0.3s'
        }}>
          <button 
            onClick={() => window.open(previewUrl, '_blank')}
            style={{
              padding: '8px 16px',
              background: 'rgba(0,0,0,0.8)',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            üìÑ Open PDF
          </button>
        </div>
      </div>
    );
  }

  // If we have a preview URL but don't know the type, try to show it anyway
  if (previewUrl && !isImage && !isPDF) {
    // Try to detect from file extension
    const ext = certificate.fileName?.toLowerCase().split('.').pop();
    if (ext === 'pdf') {
      return (
        <div className="pdf-preview-container" style={{ position: 'relative', width: '100%', height: '100%' }}>
          <iframe
            src={previewUrl}
            className="certificate-preview-pdf"
            title={certificate.certificateName || certificate.fileName}
            style={{ width: '100%', height: '100%', border: 'none' }}
          />
        </div>
      );
    } else if (['jpg', 'jpeg', 'png', 'gif'].includes(ext)) {
      return (
        <div className="image-preview-container">
          <img
            src={previewUrl}
            alt={certificate.certificateName || certificate.fileName}
            className="certificate-preview-image"
            style={{ width: '100%', height: '100%', objectFit: 'contain' }}
          />
        </div>
      );
    }
  }

  return (
    <div className="file-placeholder">
      <div className="file-icon">{getFileTypeIcon(certificate.fileType)}</div>
      <div className="file-info">
        <span className="file-name">{certificate.fileName}</span>
        <span className="file-type">No preview available</span>
      </div>
    </div>
  );
};

export default AuthenticatedPreview;
