import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../utils/axiosConfig';

const DiagnosticComponent = () => {
  const { user } = useAuth();
  const [diagnostics, setDiagnostics] = useState({
    authStatus: 'checking',
    apiStatus: 'checking',
    componentsStatus: 'checking',
    errors: []
  });

  useEffect(() => {
    runDiagnostics();
  }, []);

  const runDiagnostics = async () => {
    const errors = [];
    let authStatus = 'ok';
    let apiStatus = 'ok';
    let componentsStatus = 'ok';

    // Check authentication
    try {
      if (!user) {
        authStatus = 'not-authenticated';
        errors.push('User not authenticated - please login');
      } else {
        console.log('User authenticated:', user);
        // Check if token exists
        const token = localStorage.getItem('token');
        if (!token) {
          authStatus = 'error';
          errors.push('No auth token found in localStorage');
        }
      }
    } catch (error) {
      authStatus = 'error';
      errors.push(`Auth error: ${error.message}`);
    }

    // Check API connectivity
    try {
      // First test health endpoint (no auth required)
      // Use fetch directly to avoid auth interceptor
      const healthResponse = await fetch('/api/health');
      if (healthResponse.ok) {
        const data = await healthResponse.json();
        console.log('Health check:', data);
      } else {
        throw new Error(`Health check failed: ${healthResponse.status}`);
      }
      
      // Then test authenticated endpoint if user exists
      if (user) {
        const baseUrl = user.role === 'STUDENT' ? '/api/student/certificates' : '/api/staff/certificates';
        const response = await api.get(baseUrl);
        console.log('API response:', response.data);
      }
    } catch (error) {
      apiStatus = 'error';
      if (error.response) {
        errors.push(`API error: ${error.response.status} - ${error.response.data?.error || error.response.statusText}`);
      } else if (error.request) {
        errors.push('API error: No response from server - check if backend is running');
      } else {
        errors.push(`API error: ${error.message}`);
      }
      console.error('API Error:', error);
    }

    // Check localStorage and basic browser features
    try {
      // Check localStorage
      if (!window.localStorage) {
        componentsStatus = 'error';
        errors.push('localStorage not available');
      }
      
      // Check if React Router is working
      if (!window.location) {
        componentsStatus = 'error';
        errors.push('Navigation not available');
      }
      
      console.log('Component checks passed');
    } catch (error) {
      componentsStatus = 'error';
      errors.push(`Component error: ${error.message}`);
    }

    setDiagnostics({
      authStatus,
      apiStatus,
      componentsStatus,
      errors
    });
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'ok': return '‚úÖ';
      case 'error': return '‚ùå';
      case 'checking': return '‚è≥';
      default: return '‚ö†Ô∏è';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'ok': return '#10b981';
      case 'error': return '#ef4444';
      case 'checking': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: '10px',
      right: '10px',
      background: 'white',
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      padding: '16px',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      zIndex: 9999,
      minWidth: '300px',
      fontSize: '14px'
    }}>
      <h3 style={{ margin: '0 0 12px 0', fontSize: '16px' }}>üîç System Diagnostics</h3>
      
      <div style={{ marginBottom: '8px' }}>
        <span style={{ color: getStatusColor(diagnostics.authStatus) }}>
          {getStatusIcon(diagnostics.authStatus)} Authentication: {diagnostics.authStatus}
        </span>
        {user && (
          <div style={{ fontSize: '12px', color: '#6b7280', marginLeft: '20px' }}>
            User: {user.fullName} ({user.role})
          </div>
        )}
      </div>

      <div style={{ marginBottom: '8px' }}>
        <span style={{ color: getStatusColor(diagnostics.apiStatus) }}>
          {getStatusIcon(diagnostics.apiStatus)} API Connection: {diagnostics.apiStatus}
        </span>
      </div>

      <div style={{ marginBottom: '8px' }}>
        <span style={{ color: getStatusColor(diagnostics.componentsStatus) }}>
          {getStatusIcon(diagnostics.componentsStatus)} Components: {diagnostics.componentsStatus}
        </span>
      </div>

      {diagnostics.errors.length > 0 && (
        <div style={{ marginTop: '12px' }}>
          <strong style={{ color: '#ef4444' }}>Errors:</strong>
          <ul style={{ margin: '4px 0', paddingLeft: '20px', fontSize: '12px' }}>
            {diagnostics.errors.map((error, index) => (
              <li key={index} style={{ color: '#ef4444', marginBottom: '2px' }}>
                {error}
              </li>
            ))}
          </ul>
        </div>
      )}

      <div style={{ marginTop: '12px', display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
        <button 
          onClick={runDiagnostics}
          style={{
            background: '#3b82f6',
            color: 'white',
            border: 'none',
            padding: '6px 12px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '12px'
          }}
        >
          üîÑ Refresh
        </button>
        
        <button 
          onClick={() => {
            localStorage.clear();
            window.location.reload();
          }}
          style={{
            background: '#f59e0b',
            color: 'white',
            border: 'none',
            padding: '6px 12px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '12px'
          }}
        >
          üßπ Clear & Reload
        </button>
        
        <button 
          onClick={() => window.open('/test-api.html', '_blank')}
          style={{
            background: '#10b981',
            color: 'white',
            border: 'none',
            padding: '6px 12px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '12px'
          }}
        >
          üß™ API Test
        </button>
      </div>
    </div>
  );
};

export default DiagnosticComponent;
