import React from 'react';

const CertificateCard = ({ 
  certificate, 
  onView, 
  onDownload, 
  onDelete, 
  onReview, 
  userRole = 'STUDENT' 
}) => {
  const getStatusConfig = (status) => {
    const configs = {
      PENDING: { 
        color: 'orange', 
        icon: '‚è≥', 
        label: 'Pending Review',
        bgColor: 'rgb(245 158 11 / 0.1)',
        textColor: '#f59e0b'
      },
      VERIFIED: { 
        color: 'green', 
        icon: '‚úÖ', 
        label: 'Verified',
        bgColor: 'rgb(16 185 129 / 0.1)',
        textColor: '#10b981'
      },
      REJECTED: { 
        color: 'red', 
        icon: '‚ùå', 
        label: 'Rejected',
        bgColor: 'rgb(239 68 68 / 0.1)',
        textColor: '#ef4444'
      }
    };
    return configs[status] || configs.PENDING;
  };

  const getFileTypeIcon = (fileType) => {
    if (fileType?.includes('pdf')) return 'üìÑ';
    if (fileType?.includes('image')) return 'üñºÔ∏è';
    return 'üìé';
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const statusConfig = getStatusConfig(certificate.status);

  return (
    <div className="certificate-card-modern">
      {/* Card Header */}
      <div className="card-header">
        <div className="certificate-icon">
          {getFileTypeIcon(certificate.fileType)}
        </div>
        <div className="status-badge-modern" style={{
          backgroundColor: statusConfig.bgColor,
          color: statusConfig.textColor
        }}>
          <span className="status-icon">{statusConfig.icon}</span>
          <span className="status-text">{statusConfig.label}</span>
        </div>
      </div>

      {/* Card Content */}
      <div className="card-content">
        <h3 className="certificate-title">{certificate.certificateName}</h3>
        
        <div className="certificate-meta">
          {userRole === 'STAFF' && (
            <div className="meta-item">
              <span className="meta-label">Student:</span>
              <span className="meta-value">{certificate.studentName}</span>
            </div>
          )}
          
          <div className="meta-item">
            <span className="meta-label">Uploaded:</span>
            <span className="meta-value">{formatDate(certificate.uploadDate)}</span>
          </div>

          {certificate.metadata?.certificateType && (
            <div className="meta-item">
              <span className="meta-label">Type:</span>
              <span className="meta-value">{certificate.metadata.certificateType}</span>
            </div>
          )}

          {certificate.metadata?.issuingOrganization && (
            <div className="meta-item">
              <span className="meta-label">Issued by:</span>
              <span className="meta-value">{certificate.metadata.issuingOrganization}</span>
            </div>
          )}

          {certificate.metadata?.issueYear && (
            <div className="meta-item">
              <span className="meta-label">Year:</span>
              <span className="meta-value">{certificate.metadata.issueYear}</span>
            </div>
          )}

          {certificate.verifiedBy && (
            <div className="meta-item">
              <span className="meta-label">Verified by:</span>
              <span className="meta-value">{certificate.verifiedBy}</span>
            </div>
          )}
        </div>

        {certificate.staffRemarks && (
          <div className="remarks-section">
            <span className="remarks-label">Remarks:</span>
            <p className="remarks-text">{certificate.staffRemarks}</p>
          </div>
        )}
      </div>

      {/* Card Actions */}
      <div className="card-actions">
        <button
          className="action-btn primary"
          onClick={() => onView(certificate)}
          title="View Certificate"
        >
          <span className="btn-icon">üëÅÔ∏è</span>
          <span className="btn-text">View</span>
        </button>

        <button
          className="action-btn secondary"
          onClick={() => onDownload(certificate.certificateId, certificate.fileName)}
          title="Download Certificate"
        >
          <span className="btn-icon">üì•</span>
          <span className="btn-text">Download</span>
        </button>

        {userRole === 'STAFF' && (
          <button
            className="action-btn accent"
            onClick={() => onReview(certificate)}
            title="Review Certificate"
          >
            <span className="btn-icon">üìã</span>
            <span className="btn-text">Review</span>
          </button>
        )}

        {userRole === 'STUDENT' && certificate.status === 'REJECTED' && (
          <button
            className="action-btn danger"
            onClick={() => onDelete(certificate.certificateId)}
            title="Delete Certificate"
          >
            <span className="btn-icon">üóëÔ∏è</span>
            <span className="btn-text">Delete</span>
          </button>
        )}
      </div>

      {/* Progress Indicator for Pending */}
      {certificate.status === 'PENDING' && (
        <div className="progress-indicator">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: '33%' }}></div>
          </div>
          <span className="progress-text">Awaiting Review</span>
        </div>
      )}
    </div>
  );
};

export default CertificateCard;
